:root {
    --temp-hue: 200;
    --temp-alpha: 100%;
    --temp-rotation: 180deg;
  }
  
  
  
  .app {
      position: absolute;
      top: 65%;
      left: 50%;
      transform: translate(-50% ,-60%);
    display: flex;
  
    justify-content: space-around;
    // width: 80vmin;
    // height: 80vmin;
    width: 250px;
    height: 250px;
    border-radius: 40px;
  
    // flex: 0 0 auto;
  }
  
  .dial-container {
    position: relative;
    width: 250px;
    height: 250px;
  }
  
  .dial {
    filter: drop-shadow(
      -20px 30px 40px hsla(var(--temp-hue), 85%, 80%, var(--temp-alpha))
    );
    position: relative;
    z-index: 2;
    user-select: none;
  }
  
  .dial__tick {
    stroke: rgba(0, 0, 0, 0.6);
    stroke-width: 1;
  }
  
  .dial__center {
    fill: #fff;
  /*   filter: drop-shadow(20px 30px 40px rgb(10, 10, 10)); */
  }
  
  .dial__temp {
    font-size: 2.5rem;
    font-weight: 400;
    fill: #29273e;
     dominant-baseline: central;
    font-variant-numeric: tabular-nums;
  }
  
  .dial__track {
    fill: none;
    transform-origin: 50% 50%;
  }
  
  .dial__temp-units {
    fill: #b5b4bc;
  }
  
  .dial__dot {
    
    fill: $navActive;
    cursor: pointer;
  }
  
  .dial__control {
    transform: rotate(var(--temp-rotation));
    transform-origin: 50% 50%;
  }
  
